import React from "react";
import { Navbar, Container, Nav, NavDropdown, Button, Form, FormControl} from "react-bootstrap"; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import './NavBar.css';

export default function NavBar(props) {
    const [formInput, setFormInput] = useState('');
    const [username, setUsername] = useState(null);
    const navigate = useNavigate();

    useEffect(function() {
        axios.get('/api/user/isLoggedIn')
        .then(response => setUsername(response.data.username))
        .catch(error => console.log("User is not logged in " + username));
    }, [logout()])

    function logout(){
        axios.post('/api/user/logout')
        .then(reponse => {
            navigate('/')
        })
        .catch(error => console.log('Error logging out'));
    }

    function underDropDown(){
        if (username){
            return (
                <NavDropdown title={username} id="navbarScrollingDropdown">
                <NavDropdown.Item href={"/userAccount/" + username} >User Account</NavDropdown.Item>
                <NavDropdown.Item href="#action4">My Reviews</NavDropdown.Item>
                <NavDropdown.Item href="/" onClick={logout}>Log Out</NavDropdown.Item>
                </NavDropdown>
            )
        } else {
            return (
                <NavDropdown title='Register Or Log In' id="navbarScrollingDropdown">
                <NavDropdown.Item href='/createUser'>Create New Account</NavDropdown.Item>
                <NavDropdown.Item href="/login">Log In</NavDropdown.Item>
                </NavDropdown>
            )
        }
    }

    return (
        <div className='navBar'>
        <Navbar bg="light" expand="lg" >
        <Container fluid className="nana">
            <Navbar.Brand href="#">FoodReviewApp</Navbar.Brand>
            <Navbar.Toggle aria-controls="navbarScroll" />
            <Navbar.Collapse id="navbarScroll">
            <Nav
                className="me-auto my-2 my-lg-0"
                style={{ maxHeight: '100px' }}
                navbarScroll
            >
                <Nav.Link href="#action1">Home</Nav.Link>
                <Nav.Link href="#action2">Top Dish</Nav.Link>
                {underDropDown()}
            </Nav>
            <Form className="d-flex">
                <FormControl
                type="text"
                value={formInput}
                onChange={(e) => setFormInput(e.target.value)}
                placeholder="Search"
                className="me-2"
                aria-label="Search"
                />
                <Button 
                variant="outline-success"
                >
                Search</Button>
            </Form>
            </Navbar.Collapse>
        </Container>
        </Navbar>
        </div>
    )
}
