import axios from 'axios';
import React, {useState, useEffect} from 'react';
import { useParams } from 'react-router';
import NavBar from './NavBar';
import './DishDetail.css';

export default function DishDetail(props){
    const[dish, setDish] = useState(undefined);
    const[commentList, setCommentList] = useState([]);
    const param = useParams();

    useEffect(() => {
        axios.get('/api/dish/' + param.dishId)
        .then(function(response){
            setDish(response.data);
        })
        findAllComment()
        } ,[]);

    function findAllComment() {
        axios.get('/api/comment/dishId/' + param.dishId)
        .then(function(response){
            setCommentList(response.data);
        })
    }

    if (!dish){
        return (<div>
            Dish not found
        </div>)
    }

    const dishDetailComponent = [];
    for (let comment of commentList){
        dishDetailComponent.unshift(
            <div className='commentContainer' key={comment._id}>
                <h3>Username: {comment.username}</h3>
                <h1>Commennt: {comment.comment}</h1>
                <h3>Rank: {comment.rank}</h3>  
            </div>
    )}

    return (
        <div>
            <NavBar/>
            <div className='mainDetailContainer'>
                <div className='reviewContainer' key={dish._id}>
                    <h3>Username: {dish.username}</h3>
                    <h3>Rank: {dish.rank}</h3> 
                </div>
                {dishDetailComponent}
            </div>
        </div>
    )
}
